import json
from json.decoder import JSONDecodeError
import re


class AttributeCheck:

	@staticmethod
	def isJSONValid3(json_in_string: str) -> bool:
		try:
			json.loads(json_in_string)
			return True
		except JSONDecodeError:
			return False

	def objectNotNull(*objects: object) -> bool:
		size=len(objects)
		for i in range(0,size):
			if objects[i]==None:
				return False
		return True
	
	def stringsNotNull(*strings: str) -> bool:
		size=len(strings)
		for i in range(0,size):
			if strings[i] is None or strings[i]=="" or strings[i]=="null" or strings[i]=="undefined" or strings[i]=="[]":
				return False
		return True

	def isUppercase(*strings: str) -> bool:
		for string in strings:
			if not string.isupper():
				return False
		return True
	
	def isInteger(*strings: str) -> bool:
		for string in strings:
			if string.lstrip("+-").isdigit()==False:
				return False
		return True

	
	def isDouble(*strings: str) -> bool:
		for string in strings:
			try:
				float(string)
			except ValueError:
				return False
		return True


	def integerNotNull_Zero(*integers: int) -> bool:
		for i in integers:
			if i==None or i==0:
				return False
		return True
	

	def listNotEmpty(*lists: list) -> bool:
		for l in lists:
			if l==None or l==[]:
				return False
		return True

	def setNotNull_Zero(*sets: set) -> bool:
		for s in sets:
			if s==None or len(s)==0:
				return False
		return True

	def dictNotNull_Zero(*dicts: dict) -> bool:
		for d in dicts:
			if d==None or d=={}:
				return False
		return True


