	def isPhone(phone: str) -> bool:
		regex=r"\\d{10}|(?:\\d{3}-){2}\\d{4}|\\(\\d{3}\\)\\d{3}-?\\d{4}"
		if re.match(re.compile(regex),phone)==None:
			return False
		return True
	

